byChapter.map:
function(doc) {
    if (doc.subject && doc.chapter)
      emit([doc.subject, doc.chapter]);
    else if (doc.chapter)
      emit(["Mathematik", doc.chapter]);
}

links.map:
function(doc) {
    if (doc.links)
        emit(doc.subject + "." + doc.chapter + "." + doc.topic, doc.subject + "." + doc.links);
}

count.map:
function(doc) {
  if (!doc.links && doc.topic != "_")
    emit(doc.subject + "." + doc.chapter, doc.subject + "." + doc.chapter);
}

byModule.map:
function(doc) {
    if (doc.subject && doc.module)
      emit([doc.subject, doc.module]);
    else if (doc.module)
      emit(["Mathematik", doc.module]);
}

byTopic.map:
function(doc) {
    if (doc.subject && doc.chapter && doc.topic)
      emit([doc.subject, doc.chapter, doc.topic]);
}

availableModules.map:
function(doc) {
    if (doc.subject && doc.module)
      emit([doc.subject, doc.module], 1);
    else if (doc.module)
      emit(["Mathematik", doc.module], 1);
}

deps.map:
function(doc) {
    if (doc.links && doc.depends) {
        for (var i = 0; i < doc.depends.length; i++)
            emit(doc.subject + "." + doc.links, doc.subject + "." + doc.chapter + "." + doc.depends[i]);
    }
}

topics.map:
function(doc) {
    if (!doc.links)
        emit([doc.subject, doc.chapter], doc.chapter + "." + doc.topic);
}

topics.index:
function(doc) {
    if ('_' === doc.topic) return;
    index("chapter", doc.chapter, {'store': true});
    index("topic", doc.topic, {'store': true, 'boost': 2});
    if (doc.summary)
        index("summary", doc.summary, {'store': true, 'boost': 2});
    if (doc.description)
        index("description", doc.description, {'store': true});
    if (doc.thumb)
        index("thumb", doc.thumb, {'store': true, 'index': false});
}
