byChapterTopicKey.map:
function(doc) {
    if (doc.subject && doc.chapter && doc.topic && doc.key)
      emit([doc.subject, doc.chapter, doc.topic, doc.key]);
}

byChapterTopic.map:
function(doc) {
    if (doc.subject && doc.chapter && doc.topic)
      emit([doc.subject, doc.chapter, doc.topic]);
}

byKey.map:
function(doc) {
    if (doc.subject && doc.set && doc.key)
      emit([doc.subject, doc.set, doc.key]);
}
